import React from 'react';
import Table from "./../components/Table";
import Cognos from "./../components/Cognos";
import PropTypes from 'prop-types';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import CardSection from './CardSection';

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`nav-tabpanel-${index}`}
      aria-labelledby={`nav-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box p={3}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.any.isRequired,
  value: PropTypes.any.isRequired,
};

function a11yProps(index) {
  return {
    id: `nav-tab-${index}`,
    'aria-controls': `nav-tabpanel-${index}`,
  };
}

function LinkTab(props) {
  return (
    <Tab
      component="a"
      onClick={(event) => {
        event.preventDefault();
      }}
      {...props}
    />
  );
}

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.default,
    color: 'black',
  },
  label: {
    color: 'white',
    backgroundColor: 'black',
  },
}));

export default function NavTabs() {
  const classes = useStyles();
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <div className={classes.root}>
      <AppBar position="static">
      <span className={classes.label}>
        <Tabs
          variant="fullWidth"
          value={value}
          onChange={handleChange}
          aria-label="nav tabs example"
        >

          <LinkTab label="Overview" href="/drafts" {...a11yProps(0)} />
          <LinkTab label="Visual" href="/trash" {...a11yProps(1)} />
          {/* <LinkTab label="Watson" href="/spam" {...a11yProps(2)} /> */}

        </Tabs>
        </span>
      </AppBar>
      <TabPanel color = 'white' value={value} index={0}>
        <CardSection />
        <Table />


      </TabPanel>
      <TabPanel value={value} index={1}>
        <Cognos />
      </TabPanel>
      {/* <TabPanel value={value} index={2}>
        Watson
      </TabPanel> */}
    </div>
  );
}
